import { createContext, useState } from "react";


const FavouriteContext=createContext({
    Favourites: [],
    totalFavourites: 0,
    addFavorite: (favoriteMetUp) => {},
    removeFavorite: (favoriteMetUp) => {},
    itemIsFavorite: (favoriteMetUp) => {}
});

 export function FavouriteContextProvider(props){
    const [userFavourites,setuserFavourites]=useState([]);

     const addFavoriteHandler=(favoriteMetUp)=>{
         setuserFavourites(prevuserFavourites=>{
            return prevuserFavourites.concat(favoriteMetUp)
         })

     }
     const removeFavoriteHandler=(meetUpId)=>{
        setuserFavourites(prevuserFavourites=>{
           return prevuserFavourites.filter(meetup => meetup.id !== meetUpId)
     })
    }
     const itemIsFavoriteHandler=(meetUpId)=>{
        setuserFavourites(prevuserFavourites=>{
           return prevuserFavourites.some(meetup => meetup.id === meetUpId)
     })
    }

    const context={
        Favourites:userFavourites,
        totalFavourites:userFavourites.length,
        addFavorite: addFavoriteHandler,
        removeFavorite: removeFavoriteHandler,
        itemIsFavorite: itemIsFavoriteHandler,

    }

    return(
        <FavouriteContext.Provider value={context} >
        {props.children}
        </FavouriteContext.Provider>
    );
}
export default FavouriteContext;
